import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';

const HomeView = () => {
  const [selectedTable, setSelectedTable] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);

  const handleTableSelect = (event) => {
    setSelectedTable(event.target.value);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleOpenForm = () => {
    setShowForm(true);
  };

  const handleCloseForm = () => {
    setShowForm(false);
  };

  return (
    <div>
      <header>
        <h1>Projeto Final - Gerenciador de Tabelas</h1>
        <div className="top-bar">
          <select onChange={handleTableSelect}>
            <option value="">Selecione uma Tabela</option>
            <option value="alunos">Alunos</option>
            <option value="disciplinas">Disciplinas</option>
            <option value="professores">Professores</option>
            <option value="salas">Salas</option>
            <option value="turmas">Turmas</option>
          </select>
          <input
            type="text"
            placeholder="Pesquisar..."
            value={searchTerm}
            onChange={handleSearchChange}
            disabled={!selectedTable}
          />
          <button onClick={handleOpenForm} disabled={!selectedTable}>
            Inserir Dados
          </button>
        </div>
      </header>
      <main>
        {showForm ? (
          <FormModal table={selectedTable} onClose={handleCloseForm} />
        ) : (
          <Table table={selectedTable} searchTerm={searchTerm} />
        )}
      </main>
    </div>
  );
};

export default HomeView;
