import React, { useState, useEffect } from 'react';

const FormModal = ({ table, onClose, existingData = null, onSave }) => {
  const [formData, setFormData] = useState(existingData || {});
  const [relatedData, setRelatedData] = useState({
    professores: [],
    salas: [],
    disciplinas: [],
    alunos: [],
  });

  useEffect(() => {
    if (table === 'turmas') {
      fetchRelatedData();
    }
  }, [table]);

  const fetchRelatedData = () => {
    const endpoints = ['professores', 'salas', 'disciplinas', 'alunos'];
    Promise.all(
      endpoints.map((endpoint) =>
        fetch(`http://localhost:5000/api/${endpoint}`)
          .then((res) => res.json())
          .catch((err) => console.error(err))
      )
    ).then(([professores, salas, disciplinas, alunos]) => {
      setRelatedData({ professores, salas, disciplinas, alunos });
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{existingData ? 'Editar Registro' : 'Inserir Registro'}</h2>
        <form onSubmit={handleSubmit}>
          {table === 'turmas' ? (
            <>
              <div>
                <label>Nome</label>
                <input
                  type="text"
                  name="nome"
                  value={formData.nome || ''}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label>Professor</label>
                <select
                  name="professores_id"
                  value={formData.professores_id || ''}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione um professor</option>
                  {relatedData.professores.map((prof) => (
                    <option key={prof.id} value={prof.id}>
                      {prof.nome}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label>Sala</label>
                <select
                  name="salas_id"
                  value={formData.salas_id || ''}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione uma sala</option>
                  {relatedData.salas.map((sala) => (
                    <option key={sala.id} value={sala.id}>
                      {sala.nome}
                    </option>
                  ))}
                </select>
              </div>
              <div>
                <label>Disciplina</label>
                <select
                  name="disciplinas_id"
                  value={formData.disciplinas_id || ''}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione uma disciplina</option>
                  {relatedData.disciplinas.map((disc) => (
                    <option key={disc.id} value={disc.id}>
                      {disc.nome}
                    </option>
                  ))}
                </select>
              </div>
            </>
          ) : (
            <>
              {Object.keys(formData).map((key) => (
                <div key={key}>
                  <label>{key}</label>
                  <input
                    type="text"
                    name={key}
                    value={formData[key] || ''}
                    onChange={handleChange}
                  />
                </div>
              ))}
            </>
          )}
          <div className="form-actions">
            <button type="submit">Salvar</button>
            <button type="button" onClick={onClose}>
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormModal;
