import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';
import ReactivationModal from './ReactivationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showFormModal, setShowFormModal] = useState(false);
  const [formData, setFormData] = useState(null);
  const [showReactivationModal, setShowReactivationModal] = useState(false);

  const handleTableChange = (e) => setTable(e.target.value);
  const handleSearchChange = (e) => setSearchTerm(e.target.value);

  const handleEdit = (row) => {
    setFormData(row);
    setShowFormModal(true);
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}`, { method: 'DELETE' })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao excluir o registro.');
        alert('Registro excluído com sucesso!');
      })
      .catch((err) => alert(err.message));
  };

  const handleViewRelated = (relatedTable, id, title) => {
    alert(`Mostrando ${title} relacionados ao ID ${id}`);
    // Aqui você pode abrir outro modal ou manipular a exibição dos dados relacionados
  };

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <select onChange={handleTableChange} style={{ marginRight: '10px' }}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar por nome..."
          value={searchTerm}
          onChange={handleSearchChange}
          style={{ marginRight: '10px' }}
        />
        <button
          onClick={() => {
            setFormData(null);
            setShowFormModal(true);
          }}
          disabled={!table}
          style={{ marginLeft: '10px' }}
        >
          Inserir
        </button>
      </div>
      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          onEdit={handleEdit}
          onDelete={handleDelete}
          onViewRelated={handleViewRelated}
        />
      )}
      <button
        onClick={() => setShowReactivationModal(true)}
        style={{ position: 'fixed', bottom: '20px', right: '20px' }}
      >
        Reativar Itens
      </button>
      {showFormModal && (
        <FormModal
          table={table}
          data={formData}
          onClose={() => setShowFormModal(false)}
        />
      )}
      {showReactivationModal && (
        <ReactivationModal
          table={table}
          onClose={() => setShowReactivationModal(false)}
        />
      )}
    </div>
  );
};

export default HomeView;
