import React, { useEffect, useState } from 'react';
import { FaEdit, FaTrashAlt, FaUsers } from 'react-icons/fa';

const Table = ({ table, searchTerm, refreshKey, onEdit, onDeactivate, onViewRelated }) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (table) {
      fetchData();
    }
  }, [table, searchTerm, refreshKey]);

  const fetchData = () => {
    setIsLoading(true);
    const queryParam = searchTerm ? `?name=${searchTerm}` : '';
    const url = `http://localhost:5000/api/${table}${queryParam}`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao carregar dados.');
        }
        return response.json();
      })
      .then((data) => setData(data))
      .catch((err) => setError(err.message))
      .finally(() => setIsLoading(false));
  };

  if (isLoading) return <p>Carregando dados...</p>;
  if (error) return <p className="error-message">{error}</p>;

  return (
    <table className="data-table">
      <thead>
        <tr>
          {data.length > 0 &&
            Object.keys(data[0])
              .filter((key) => key !== 'id')
              .map((col) => (
                <th key={col}>{col.toUpperCase()}</th>
              ))}
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        {data.length > 0 ? (
          data.map((row) => (
            <tr key={row.id}>
              {Object.keys(row)
                .filter((key) => key !== 'id')
                .map((col) => (
                  <td key={col}>{row[col]}</td>
                ))}
              <td>
                <FaEdit
                  className="action-icon edit-icon"
                  onClick={() => onEdit(row)}
                />
                <FaTrashAlt
                  className="action-icon delete-icon"
                  onClick={() => onDeactivate(row.id)}
                />
                <FaUsers
                  className="action-icon related-icon"
                  onClick={() =>
                    onViewRelated(
                      table === 'turmas' ? 'alunos' : 'turmas',
                      row.id,
                      `Relacionados com ${row.nome || row.turma}`
                    )
                  }
                />
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan="100%" className="no-data">
              Nenhum dado encontrado.
            </td>
          </tr>
        )}
      </tbody>
    </table>
  );
};

export default Table;
