import React, { useState, useEffect } from 'react';
import Table from './Table';
import RelationModal from './RelationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [data, setData] = useState([]);
  const [relatedId, setRelatedId] = useState(null);
  const [relationType, setRelationType] = useState('');
  const [showRelationModal, setShowRelationModal] = useState(false);

  useEffect(() => {
    if (table) {
      fetchTableData();
    }
  }, [table]);

  const fetchTableData = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/${table}`);
      const result = await response.json();
      setData(result);
    } catch (err) {
      console.error('Erro ao buscar dados:', err.message);
    }
  };

  const handleTableChange = (e) => {
    setTable(e.target.value);
    setData([]);
  };

  const handleViewRelations = (id, type) => {
    setRelatedId(id);
    setRelationType(type);
    setShowRelationModal(true);
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/${table}/${id}`, { method: 'DELETE' });
      fetchTableData(); // Atualiza os dados ap처s exclus찾o
    } catch (err) {
      console.error('Erro ao excluir registro:', err.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Gerenciamento de Tabelas</h1>
      <select onChange={handleTableChange} value={table} style={{ marginBottom: '20px' }}>
        <option value="">Selecione uma tabela</option>
        <option value="alunos">Alunos</option>
        <option value="turmas">Turmas</option>
      </select>
      {table && (
        <Table
          table={table}
          data={data}
          onViewRelations={handleViewRelations}
          onEdit={(row) => console.log('Editando:', row)}
          onDelete={handleDelete}
          onDeactivate={(id) => console.log('Desativando:', id)}
          onReactivate={(id) => console.log('Reativando:', id)}
        />
      )}
      {showRelationModal && (
        <RelationModal
          relatedId={relatedId}
          relationType={relationType}
          onClose={() => setShowRelationModal(false)}
        />
      )}
    </div>
  );
};

export default HomeView;
