import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';
import ReactivationModal from './ReactivationModal';

const HomeView = () => {
  const [table, setTable] = useState(''); // Tabela selecionada
  const [searchTerm, setSearchTerm] = useState(''); // Termo de pesquisa
  const [showFormModal, setShowFormModal] = useState(false); // Controle do modal de inserção
  const [showReactivationModal, setShowReactivationModal] = useState(false); // Controle do modal de reativação

  const handleTableChange = (e) => {
    setTable(e.target.value);
  };

  return (
    <div>
      {/* Barra superior */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <select onChange={handleTableChange} style={{ marginRight: '10px' }}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <button
          onClick={() => setShowFormModal(true)}
          disabled={!table} // Desabilita o botão se nenhuma tabela estiver selecionada
        >
          Inserir
        </button>
      </div>

      {/* Tabela de resultados */}
      {table && <Table table={table} searchTerm={searchTerm} />}

      {/* Botão para abrir o modal de reativação */}
      <button
        onClick={() => setShowReactivationModal(true)}
        style={{ position: 'fixed', bottom: '20px', right: '20px' }}
      >
        Reativar Itens
      </button>

      {/* Modal de inserção */}
      {showFormModal && (
        <FormModal
          table={table}
          onClose={() => setShowFormModal(false)}
        />
      )}

      {/* Modal de reativação */}
      {showReactivationModal && (
        <ReactivationModal
          table={table}
          onClose={() => setShowReactivationModal(false)}
        />
      )}
    </div>
  );
};

export default HomeView;
