import React, { useState } from 'react';
import Table from './Table';
import RelationModal from './RelationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [status, setStatus] = useState('ativo'); // "ativo" ou "inativo"
  const [relationProps, setRelationProps] = useState(null);

  const handleTableChange = (e) => {
    setTable(e.target.value);
    setSearchTerm('');
  };

  const toggleStatus = () => {
    setStatus(status === 'ativo' ? 'inativo' : 'ativo');
  };

  const handleViewRelated = (relatedTable, mainId, title) => {
    setRelationProps({ relatedTable, mainId, title });
  };

  return (
    <div className="home-view">
      <div className="top-bar">
        <select onChange={handleTableChange} value={table}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={toggleStatus}>
          {status === 'ativo' ? 'Ver Inativos' : 'Ver Ativos'}
        </button>
      </div>

      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          status={status}
          onViewRelated={handleViewRelated}
        />
      )}

      {relationProps && (
        <RelationModal
          mainTable={table}
          relatedTable={relationProps.relatedTable}
          mainId={relationProps.mainId}
          title={relationProps.title}
          onClose={() => setRelationProps(null)}
        />
      )}
    </div>
  );
};

export default HomeView;
