import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';
import ReactivationModal from './ReactivationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showFormModal, setShowFormModal] = useState(false);
  const [showReactivationModal, setShowReactivationModal] = useState(false);
  const [editingData, setEditingData] = useState(null);
  const [refreshKey, setRefreshKey] = useState(0);

  const handleTableChange = (e) => {
    setTable(e.target.value);
    setSearchTerm('');
  };

  const handleDeactivate = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao desativar registro.');
        }
        alert('Registro desativado com sucesso.');
        setRefreshKey((prevKey) => prevKey + 1);
      })
      .catch((err) => console.error(err.message));
  };

  const handleSave = (formData) => {
    const method = formData.id ? 'PUT' : 'POST';
    const url = formData.id
      ? `http://localhost:5000/api/${table}/${formData.id}`
      : `http://localhost:5000/api/${table}`;

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao salvar registro.');
        }
        alert('Registro salvo com sucesso.');
        setShowFormModal(false);
        setRefreshKey((prevKey) => prevKey + 1);
      })
      .catch((err) => console.error(err.message));
  };

  return (
    <div className="home-view">
      <div className="toolbar">
        <select onChange={handleTableChange} value={table}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button
          onClick={() => {
            setEditingData(null);
            setShowFormModal(true);
          }}
          disabled={!table}
        >
          Inserir
        </button>
      </div>

      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          refreshKey={refreshKey}
          onEdit={(row) => {
            setEditingData(row);
            setShowFormModal(true);
          }}
          onDeactivate={handleDeactivate}
          onViewRelated={(relatedTable, id, title) =>
            console.log(`Ver ${relatedTable} relacionados com ID ${id} (${title})`)
          }
        />
      )}

      <button
        className="reactivate-button"
        onClick={() => setShowReactivationModal(true)}
      >
        Reativar Itens
      </button>

      {showFormModal && (
        <FormModal
          table={table}
          onClose={() => setShowFormModal(false)}
          existingData={editingData}
          onSave={handleSave}
        />
      )}

      {showReactivationModal && (
        <ReactivationModal
          table={table}
          onClose={() => setShowReactivationModal(false)}
          onReactivationSuccess={() => setRefreshKey((prevKey) => prevKey + 1)}
        />
      )}
    </div>
  );
};

export default HomeView;
