import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';
import ReactivationModal from './ReactivationModal';
import RelationModal from './RelationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showFormModal, setShowFormModal] = useState(false);
  const [showReactivationModal, setShowReactivationModal] = useState(false);
  const [relationModal, setRelationModal] = useState({
    show: false,
    relatedId: null,
    relatedTable: '',
    title: '',
  });

  const handleTableChange = (e) => {
    setTable(e.target.value);
  };

  const handleViewRelated = (relatedTable, relatedId, title) => {
    setRelationModal({
      show: true,
      relatedId,
      relatedTable,
      title,
    });
  };

  const handleAddOrEdit = (row) => {
    // Lógica para abrir modal de inserção ou edição com os dados do registro selecionado
    setShowFormModal(true);
  };

  const handleDelete = (id) => {
    // Lógica para desativar ou excluir o registro
    console.log(`Deletar registro com ID ${id}`);
  };

  return (
    <div style={{ padding: '20px' }}>
      {/* Barra superior */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <select onChange={handleTableChange} style={{ marginRight: '10px' }}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginRight: '10px', flex: 1 }}
        />
        <button
          onClick={() => setShowFormModal(true)}
          disabled={!table}
          style={{
            padding: '8px 12px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          Inserir
        </button>
      </div>

      {/* Tabela */}
      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          onEdit={handleAddOrEdit}
          onDelete={handleDelete}
          onViewRelated={handleViewRelated}
        />
      )}

      {/* Botão de Reativar */}
      <button
        onClick={() => setShowReactivationModal(true)}
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          padding: '10px 20px',
          backgroundColor: '#28a745',
          color: '#fff',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
      >
        Reativar Itens
      </button>

      {/* Modais */}
      {showFormModal && <FormModal table={table} onClose={() => setShowFormModal(false)} />}
      {showReactivationModal && (
        <ReactivationModal table={table} onClose={() => setShowReactivationModal(false)} />
      )}
      {relationModal.show && (
        <RelationModal
          relatedId={relationModal.relatedId}
          relatedTable={relationModal.relatedTable}
          title={relationModal.title}
          onClose={() =>
            setRelationModal({ show: false, relatedId: null, relatedTable: '', title: '' })
          }
        />
      )}
    </div>
  );
};

export default HomeView;
