import React, { useEffect, useState } from 'react';
import { FaEdit, FaTrashAlt, FaCheck } from 'react-icons/fa';

const Table = ({ table, searchTerm, status, onEdit, onReactivate }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchData();
  }, [table, searchTerm, status]);

  const fetchData = () => {
    setIsLoading(true);
    const queryParam = `?status=${status}&name=${searchTerm}`;
    const url = `http://localhost:5000/api/${table}${queryParam}`;
    fetch(url)
      .then((response) => {
        if (!response.ok) throw new Error(`Erro ao buscar dados: ${response.statusText}`);
        return response.json();
      })
      .then((data) => setData(data))
      .catch((err) => setError(err.message))
      .finally(() => setIsLoading(false));
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p className="error">{error}</p>;
  }

  const columns = data.length > 0 ? Object.keys(data[0]) : [];

  return (
    <table className="data-table">
      <thead>
        <tr>
          {columns.map((col) => (
            <th key={col}>{col.toUpperCase()}</th>
          ))}
          <th>AÇÕES</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row) => (
          <tr key={row.id}>
            {columns.map((col) => (
              <td key={col}>{row[col]}</td>
            ))}
            <td>
              {status === 'inativo' ? (
                <FaCheck
                  className="reactivate-button"
                  onClick={() => onReactivate(row.id)}
                />
              ) : (
                <>
                  <FaEdit onClick={() => onEdit(row)} />
                  <FaTrashAlt onClick={() => console.log(`Desativar ${row.id}`)} />
                </>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
