import React, { useState } from 'react';

const FormModal = ({ table, onClose }) => {
  const [formData, setFormData] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`/api/${table}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Erro ao inserir registro');
        }
        alert('Registro inserido com sucesso!');
        onClose(); // Fecha o modal
      })
      .catch((err) => console.error('Erro ao inserir registro:', err));
  };

  return (
    <div style={{ position: 'fixed', top: '20%', left: '30%', background: '#fff', padding: '20px', border: '1px solid #ccc' }}>
      <h2>Inserir Registro</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome:</label>
          <input name="nome" onChange={handleChange} required />
        </div>
        {table === 'alunos' && (
          <div>
            <label>CPF:</label>
            <input name="CPF" onChange={handleChange} required />
          </div>
        )}
        <button type="submit">Salvar</button>
        <button type="button" onClick={onClose}>
          Cancelar
        </button>
      </form>
    </div>
  );
};

export default FormModal;
