import React, { useEffect, useState } from 'react';
import { FaEdit, FaTrashAlt, FaUsers } from 'react-icons/fa';

const Table = ({ table, searchTerm, status, onEdit, onToggleStatus, onViewRelated }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchData();
  }, [table, searchTerm, status]);

  const fetchData = () => {
    setIsLoading(true);
    const queryParam = `?status=${status}&name=${searchTerm}`;
    const url = `http://localhost:5000/api/${table}${queryParam}`;
    fetch(url)
      .then((response) => {
        if (!response.ok) throw new Error(`Erro ao buscar dados: ${response.statusText}`);
        return response.json();
      })
      .then((data) => setData(data))
      .catch((err) => setError(err.message))
      .finally(() => setIsLoading(false));
  };

  const renderActions = (row) => (
    <td>
      <FaEdit style={{ cursor: 'pointer', marginRight: '10px', color: 'blue' }} onClick={() => onEdit(row)} />
      <FaTrashAlt style={{ cursor: 'pointer', marginRight: '10px', color: 'red' }} />
      {(table === 'turmas' || table === 'alunos') && (
        <FaUsers
          style={{ cursor: 'pointer', color: 'green' }}
          onClick={() =>
            onViewRelated(
              table === 'turmas' ? 'alunos' : 'turmas',
              row.id,
              table === 'turmas'
                ? `Alunos da turma ${row.nome}`
                : `Turmas do aluno ${row.nome}`
            )
          }
        />
      )}
    </td>
  );

  if (!table) return <p>Selecione uma tabela para visualizar os dados.</p>;

  if (isLoading) return <p>Carregando dados...</p>;

  if (error) return <p className="error-message">{error}</p>;

  const columns = data.length > 0
    ? Object.keys(data[0]).filter((col) => col !== 'status')
    : [];

  return (
    <table className="data-table">
      <thead>
        <tr>
          {columns.map((col) => (
            <th key={col}>{col.toUpperCase()}</th>
          ))}
          <th>AÇÕES</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row) => (
          <tr key={row.id}>
            {columns.map((col) => (
              <td key={col}>{row[col]}</td>
            ))}
            {renderActions(row)}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
