import React, { useEffect, useState } from 'react';
import { FaEdit, FaTrashAlt, FaUsers } from 'react-icons/fa';
import RelationshipModal from './RelationshipModal';
import FormModal from './FormModal';

const Table = ({ table }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showRelationshipModal, setShowRelationshipModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [relationshipTitle, setRelationshipTitle] = useState('');

  useEffect(() => {
    if (table) {
      fetchData();
    }
  }, [table]);

  const fetchData = () => {
    setIsLoading(true);

    fetch(`http://localhost:5000/api/${table}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Erro ao buscar dados: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setData(data);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setData([]);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const handleEdit = (item) => {
    setSelectedItem(item);
    setShowEditModal(true);
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao excluir item');
        console.log('Item excluído com sucesso');
        fetchData(); // Atualizar a tabela
      })
      .catch((err) => console.error(err));
  };

  const handleViewRelated = (relatedTable, item) => {
    setSelectedItem(item);
    setRelationshipTitle(
      relatedTable === 'alunos'
        ? `Alunos da Turma ${item.turma || item.nome}`
        : `Turmas do Aluno ${item.nome}`
    );
    setShowRelationshipModal(true);
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  const isTurmas = table === 'turmas';
  const isAlunos = table === 'alunos';

  // Define colunas dinamicamente com base na tabela
  const columns = data.length > 0
    ? Object.keys(data[0]).filter((key) => key !== 'id' && key !== 'status')
    : [];

  // Adiciona a coluna "ações" para todas as tabelas
  if (data.length > 0) {
    columns.push('ações');
  }

  return (
    <div>
      <table>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col.toUpperCase()}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((row) => (
              <tr key={row.id}>
                {columns.map((col) =>
                  col === 'ações' ? (
                    <td key={col}>
                      <FaEdit
                        style={{ cursor: 'pointer', marginRight: '10px', color: 'blue' }}
                        onClick={() => handleEdit(row)}
                      />
                      <FaTrashAlt
                        style={{ cursor: 'pointer', marginRight: '10px', color: 'red' }}
                        onClick={() => handleDelete(row.id)}
                      />
                      {(isTurmas || isAlunos) && (
                        <FaUsers
                          style={{ cursor: 'pointer', color: 'green' }}
                          onClick={() =>
                            handleViewRelated(
                              isTurmas ? 'alunos' : 'turmas',
                              row
                            )
                          }
                        />
                      )}
                    </td>
                  ) : (
                    <td key={col}>{row[col]}</td>
                  )
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={columns.length}>Nenhum dado encontrado.</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Modal de edição */}
      <FormModal
        isVisible={showEditModal}
        mode="edit"
        item={selectedItem}
        table={table}
        onClose={() => setShowEditModal(false)}
        onSubmit={() => fetchData()}
      />

      {/* Modal de relacionamento */}
      <RelationshipModal
        isVisible={showRelationshipModal}
        table={isTurmas ? 'alunos' : 'turmas'}
        relatedId={selectedItem?.id}
        title={relationshipTitle}
        onClose={() => setShowRelationshipModal(false)}
      />
    </div>
  );
};

export default Table;
