import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);
  const [alunos, setAlunos] = useState([]);
  const [selectedTurmaId, setSelectedTurmaId] = useState(null);
  const [newAlunoId, setNewAlunoId] = useState('');
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // Fetch data for the selected table
  useEffect(() => {
    if (table) {
      setIsLoading(true);
      const url = `http://localhost:5000/api/${table}`;
      const queryParam = searchTerm ? `?name=${searchTerm}` : '';
      const fullUrl = url + queryParam;

      fetch(fullUrl)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Erro ao buscar dados: ${response.statusText}`);
          }
          return response.json();
        })
        .then((data) => {
          setData(data);
          setError(null);
        })
        .catch((err) => {
          console.error('Erro ao buscar dados:', err);
          setError(err.message);
          setData([]);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }, [table, searchTerm]);

  // Fetch alunos relacionados a uma turma
  const fetchAlunos = (turmaId) => {
    setIsLoading(true);
    fetch(`http://localhost:5000/api/turmas/${turmaId}/alunos`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Erro ao buscar alunos: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setAlunos(data);
        setSelectedTurmaId(turmaId);
        setError(null);
      })
      .catch((err) => {
        console.error('Erro ao buscar alunos:', err);
        setError(err.message);
        setAlunos([]);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  // Adicionar aluno à turma
  const addAlunoToTurma = () => {
    if (!newAlunoId) {
      setError('Informe o ID do aluno para adicionar.');
      return;
    }

    fetch(`http://localhost:5000/api/turmas/${selectedTurmaId}/alunos`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ alunoId: newAlunoId }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao adicionar aluno à turma.');
        }
        return response.json();
      })
      .then(() => {
        setNewAlunoId('');
        fetchAlunos(selectedTurmaId); // Atualiza a lista de alunos
        setError(null);
      })
      .catch((err) => {
        console.error('Erro ao adicionar aluno:', err);
        setError(err.message);
      });
  };

  // Remover aluno de uma turma
  const removeAlunoFromTurma = (alunoId) => {
    fetch(`http://localhost:5000/api/turmas/${selectedTurmaId}/alunos/${alunoId}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao remover aluno da turma.');
        }
        return response.json();
      })
      .then(() => {
        fetchAlunos(selectedTurmaId); // Atualiza a lista de alunos
        setError(null);
      })
      .catch((err) => {
        console.error('Erro ao remover aluno:', err);
        setError(err.message);
      });
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  const isTurmas = table === 'turmas';
  const customColumns = isTurmas
    ? ['turma', 'professor', 'sala', 'disciplina', 'dia_semana', 'horario_inicio', 'horario_termino']
    : data.length > 0
    ? Object.keys(data[0]).filter((key) => key !== 'id' && key !== 'status')
    : [];

  return (
    <>
      {/* Tabela principal */}
      <table>
        <thead>
          <tr>
            {customColumns.map((key) => (
              <th key={key}>{key}</th>
            ))}
            {isTurmas && <th>Ações</th>}
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((row, index) => (
              <tr key={index}>
                {customColumns.map((key) => (
                  <td key={key}>{row[key]}</td>
                ))}
                {isTurmas && (
                  <td>
                    <button onClick={() => fetchAlunos(row.turma_id)}>Ver Alunos</button>
                  </td>
                )}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={customColumns.length + (isTurmas ? 1 : 0)}>Nenhum dado encontrado.</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Modal de alunos relacionados */}
      {isTurmas && selectedTurmaId && (
        <div style={{ marginTop: '20px' }}>
          <h3>Alunos da Turma</h3>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nome</th>
                <th>CPF</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {alunos.length > 0 ? (
                alunos.map((aluno) => (
                  <tr key={aluno.aluno_id}>
                    <td>{aluno.aluno_id}</td>
                    <td>{aluno.aluno_nome}</td>
                    <td>{aluno.aluno_CPF}</td>
                    <td>
                      <button onClick={() => removeAlunoFromTurma(aluno.aluno_id)}>Remover</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={4}>Nenhum aluno encontrado.</td>
                </tr>
              )}
            </tbody>
          </table>
          <div>
            <input
              type="text"
              value={newAlunoId}
              onChange={(e) => setNewAlunoId(e.target.value)}
              placeholder="ID do Aluno"
            />
            <button onClick={addAlunoToTurma}>Adicionar Aluno</button>
          </div>
        </div>
      )}
    </>
  );
};

export default Table;
