import React, { useEffect, useState } from 'react';

const ReactivationModal = ({ table, onClose }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    if (table) {
      fetch(`/api/${table}/inactive`)
        .then((res) => res.json())
        .then((data) => setData(data))
        .catch((err) => console.error('Erro ao carregar dados inativos:', err));
    }
  }, [table]);

  const handleReactivate = (id) => {
    fetch(`/api/${table}/${id}/activate`, { method: 'PATCH' })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Erro ao reativar registro');
        }
        setData((prevData) => prevData.filter((item) => item.id !== id));
        alert('Registro reativado com sucesso!');
      })
      .catch((err) => console.error('Erro ao reativar registro:', err));
  };

  return (
    <div style={{ position: 'fixed', top: '20%', left: '30%', background: '#fff', padding: '20px', border: '1px solid #ccc' }}>
      <h2>Itens Inativos</h2>
      <table border="1" style={{ width: '100%' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.nome}</td>
              <td>
                <button onClick={() => handleReactivate(item.id)}>Reativar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={onClose}>Fechar</button>
    </div>
  );
};

export default ReactivationModal;
