import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]); // Dados retornados pela API
  const [error, setError] = useState(null); // Controle de erros
  const [isLoading, setIsLoading] = useState(false); // Indicador de carregamento

  useEffect(() => {
    if (table) {
      setIsLoading(true); // Ativa o carregamento
      const url = searchTerm
        ? `http://localhost:5000/api/${table}/search?name=${searchTerm}`
        : `http://localhost:5000/api/${table}`;

      fetch(url)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Erro ao buscar dados: ${response.statusText}`);
          }
          return response.json();
        })
        .then((data) => {
          setData(data); // Define os dados recebidos
          setError(null); // Remove erros
        })
        .catch((err) => {
          setError(err.message); // Registra o erro
          setData([]); // Limpa os dados em caso de erro
        })
        .finally(() => {
          setIsLoading(false); // Finaliza o carregamento
        });
    }
  }, [table, searchTerm]);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao desativar registro');
        }
        setData((prevData) => prevData.filter((item) => item.id !== id));
        alert('Registro desativado com sucesso!');
      })
      .catch((err) => console.error('Erro ao desativar registro:', err));
  };

  const handleEdit = (id) => {
    alert(`FunÃ§Ã£o de ediÃ§Ã£o chamada para ID: ${id}`);
    // Aqui vocÃª pode abrir um modal de ediÃ§Ã£o com os dados do registro
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  // Filtra as colunas para remover "id" e "status"
  const filteredColumns = data.length > 0 ? Object.keys(data[0]).filter((key) => key !== 'id' && key !== 'status') : [];

  return (
    <table>
      <thead>
        <tr>
          {filteredColumns.map((key) => (
            <th key={key}>{key}</th>
          ))}
          <th>AÃ§Ãµes</th>
        </tr>
      </thead>
      <tbody>
        {data.length > 0 ? (
          data.map((row) => (
            <tr key={row.id}>
              {filteredColumns.map((key) => (
                <td key={key}>{row[key]}</td>
              ))}
              <td>
                <button onClick={() => handleEdit(row.id)} title="Alterar">
                  ðŸ–‰
                </button>
                <button onClick={() => handleDelete(row.id)} title="Excluir">
                  ðŸ—‘
                </button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={filteredColumns.length + 1}>Nenhum dado encontrado.</td>
          </tr>
        )}
      </tbody>
    </table>
  );
};

export default Table;
