import React, { useState } from 'react';
import { FaTrashAlt, FaPlus } from 'react-icons/fa';

const RelatedModal = ({ title, data, relatedTable, onClose }) => {
  const [newItemId, setNewItemId] = useState('');

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${relatedTable}/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao excluir item');
        console.log('Item excluído com sucesso');
        // Atualizar os dados relacionados aqui
      })
      .catch((err) => console.error(err));
  };

  const handleAdd = () => {
    fetch(`http://localhost:5000/api/${relatedTable}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ relatedId: newItemId }),
    })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao adicionar item');
        console.log('Item adicionado com sucesso');
        setNewItemId('');
        // Atualizar os dados relacionados aqui
      })
      .catch((err) => console.error(err));
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h3>{title}</h3>
        <ul>
          {data.map((item) => (
            <li key={item.id}>
              {item.nome || item.turma}
              <FaTrashAlt
                style={{ cursor: 'pointer', marginLeft: '10px', color: 'red' }}
                onClick={() => handleDelete(item.id)}
              />
            </li>
          ))}
        </ul>
        <div style={{ marginTop: '20px' }}>
          <input
            type="text"
            placeholder="ID do novo item"
            value={newItemId}
            onChange={(e) => setNewItemId(e.target.value)}
            style={{ marginRight: '10px' }}
          />
          <FaPlus
            style={{ cursor: 'pointer', color: 'green' }}
            onClick={handleAdd}
          />
        </div>
        <button onClick={onClose} style={{ marginTop: '20px' }}>
          Fechar
        </button>
      </div>
    </div>
  );
};

export default RelatedModal;
