import React, { useEffect, useState } from 'react';

const RelationshipModal = ({ isVisible, table, relatedId, title, onClose }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [newId, setNewId] = useState('');

  useEffect(() => {
    if (isVisible && relatedId) {
      fetchRelatedData();
    }
  }, [isVisible, relatedId]);

  const fetchRelatedData = () => {
    fetch(`http://localhost:5000/api/${table}?relatedId=${relatedId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao carregar relacionamentos.');
        }
        return response.json();
      })
      .then((data) => setData(data))
      .catch((err) => setError(err.message));
  };

  const handleAdd = () => {
    fetch(`http://localhost:5000/api/turmaaluno`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        [table === 'alunos' ? 'turmas_id' : 'alunos_id']: relatedId,
        [table === 'alunos' ? 'alunos_id' : 'turmas_id']: newId,
      }),
    })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao adicionar relacionamento.');
        fetchRelatedData();
        setNewId('');
      })
      .catch((err) => setError(err.message));
  };

  const handleRemove = (id) => {
    fetch(`http://localhost:5000/api/turmaaluno/${id}`, { method: 'DELETE' })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao remover relacionamento.');
        fetchRelatedData();
      })
      .catch((err) => setError(err.message));
  };

  if (!isVisible) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h3>{title}</h3>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <ul>
          {data.map((item) => (
            <li key={item.id}>
              {item.nome || item.turma}
              <button onClick={() => handleRemove(item.id)} style={{ marginLeft: '10px' }}>
                Remover
              </button>
            </li>
          ))}
        </ul>
        <input
          type="text"
          placeholder={`ID do novo ${table === 'alunos' ? 'aluno' : 'turma'}`}
          value={newId}
          onChange={(e) => setNewId(e.target.value)}
        />
        <button onClick={handleAdd}>Adicionar</button>
        <button onClick={onClose} style={{ marginLeft: '10px' }}>
          Fechar
        </button>
      </div>
    </div>
  );
};

export default RelationshipModal;
