import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm, viewingInativos, onEdit, onActivate, onDeactivate }) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (table) {
      fetchData();
    }
  }, [table, searchTerm, viewingInativos]);

  const fetchData = () => {
    setIsLoading(true);
    const statusParam = viewingInativos ? 'inativos' : 'ativos';
    const queryParam = searchTerm ? `?name=${searchTerm}` : '';
    fetch(`http://localhost:5000/api/${table}?status=${statusParam}${queryParam}`)
      .then((res) => res.json())
      .then(setData)
      .catch((err) => console.error(err))
      .finally(() => setIsLoading(false));
  };

  return (
    <div className="table-container">
      <table>
        <thead>
          <tr>
            {data.length > 0 &&
              Object.keys(data[0]).map((key) => key !== 'id' && <th key={key}>{key}</th>)}
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {isLoading ? (
            <tr>
              <td colSpan={Object.keys(data[0] || {}).length + 1}>Carregando...</td>
            </tr>
          ) : data.length > 0 ? (
            data.map((item) => (
              <tr key={item.id}>
                {Object.keys(item).map(
                  (key) => key !== 'id' && <td key={`${item.id}-${key}`}>{item[key]}</td>
                )}
                <td>
                  {viewingInativos ? (
                    <button onClick={() => onActivate(item.id)}>Ativar</button>
                  ) : (
                    <button onClick={() => onDeactivate(item.id)}>Desativar</button>
                  )}
                  <button onClick={() => onEdit(item)}>Editar</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={Object.keys(data[0] || {}).length + 1}>Nenhum registro encontrado</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
