import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showFormModal, setShowFormModal] = useState(false);
  const [editingData, setEditingData] = useState(null);

  const handleTableChange = (e) => {
    setTable(e.target.value);
    setSearchTerm('');
  };

  const handleSave = (formData) => {
    const method = formData.id ? 'PUT' : 'POST';
    const url = formData.id
      ? `http://localhost:5000/api/${table}/${formData.id}`
      : `http://localhost:5000/api/${table}`;

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao salvar registro.');
        }
        alert('Registro salvo com sucesso.');
        setShowFormModal(false);
      })
      .catch((err) => console.error(err.message));
  };

  return (
    <div className="home-container">
      <div className="control-bar">
        <select onChange={handleTableChange} value={table}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={() => alert(`Pesquisar por: ${searchTerm}`)} disabled={!searchTerm}>
          Pesquisar
        </button>
        <button
          onClick={() => {
            setEditingData(null);
            setShowFormModal(true);
          }}
          disabled={!table}
        >
          Inserir
        </button>
      </div>

      {table && <Table table={table} searchTerm={searchTerm} />}

      {showFormModal && (
        <FormModal
          table={table}
          onClose={() => setShowFormModal(false)}
          existingData={editingData}
          onSave={handleSave}
        />
      )}
    </div>
  );
};

export default HomeView;
