import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';
import ReactivationModal from './ReactivationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showFormModal, setShowFormModal] = useState(false);
  const [formMode, setFormMode] = useState('create'); // 'create' ou 'edit'
  const [selectedItem, setSelectedItem] = useState(null);
  const [showReactivationModal, setShowReactivationModal] = useState(false);

  const handleTableChange = (e) => {
    setTable(e.target.value);
  };

  const handleEdit = (item) => {
    setSelectedItem(item);
    setFormMode('edit');
    setShowFormModal(true);
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}`, {
      method: 'DELETE',
    })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao excluir item');
        console.log('Item excluído com sucesso');
        // Atualizar tabela
      })
      .catch((err) => console.error(err));
  };

  const handleFormSubmit = (data) => {
    console.log(`Dados enviados (${formMode}):`, data);
    setShowFormModal(false);
  };

  return (
    <div>
      {/* Barra superior */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <select onChange={handleTableChange} style={{ marginRight: '10px' }}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <button
          onClick={() => {
            setFormMode('create');
            setShowFormModal(true);
          }}
          disabled={!table}
          style={{ marginLeft: '10px' }}
        >
          Inserir
        </button>
      </div>

      {/* Tabela */}
      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          onEdit={handleEdit}
          onDelete={handleDelete}
        />
      )}

      {/* Botão de Reativar */}
      <button
        onClick={() => setShowReactivationModal(true)}
        disabled={!table}
        style={{ position: 'fixed', bottom: '20px', right: '20px' }}
      >
        Reativar Itens
      </button>

      {/* Modais */}
      {showFormModal && (
        <FormModal
          table={table}
          mode={formMode}
          item={formMode === 'edit' ? selectedItem : null}
          onClose={() => setShowFormModal(false)}
          onSubmit={handleFormSubmit}
        />
      )}
      {showReactivationModal && (
        <ReactivationModal table={table} onClose={() => setShowReactivationModal(false)} />
      )}
    </div>
  );
};

export default HomeView;
