import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    if (table) {
      // Substitua esta parte por sua chamada real de API
      fetch(`/api/${table}`)
        .then((res) => res.json())
        .then((data) => setData(data))
        .catch((err) => console.error(err));
    }
  }, [table]);

  const filteredData = data.filter((row) =>
    Object.values(row)
      .filter((value, index) => index !== 'status') // Remove o status
      .some((value) =>
        String(value).toLowerCase().includes(searchTerm.toLowerCase())
      )
  );

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  return (
    <div>
      <h2>Tabela: {table}</h2>
      <table>
        <thead>
          <tr>
            {Object.keys(data[0] || {}).map((key) =>
              key !== 'status' ? <th key={key}>{key}</th> : null
            )}
            <th>A√ß√µes</th>
          </tr>
        </thead>
        <tbody>
          {filteredData.map((row) => (
            <tr key={row.id}>
              {Object.entries(row).map(([key, value]) =>
                key !== 'status' ? <td key={key}>{value}</td> : null
              )}
              <td>
                <button onClick={() => alert(`Alterar ID: ${row.id}`)}>
                  ‚úèÔ∏è
                </button>
                <button onClick={() => alert(`Excluir ID: ${row.id}`)}>
                  üóëÔ∏è
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
