import React, { useState } from 'react';

const ReactivationModal = ({ onClose }) => {
  const [selectedTable, setSelectedTable] = useState(null);

  const data = [
    // Simulação de dados inativos
    { id: 1, nome: 'Carlos Souza', status: 'inativo' },
  ];

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Reativar Itens</h2>
        <select onChange={(e) => setSelectedTable(e.target.value)}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
        </select>
        {selectedTable && (
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nome</th>
                <th>Status</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {data.map((row) => (
                <tr key={row.id}>
                  <td>{row.id}</td>
                  <td>{row.nome}</td>
                  <td>{row.status}</td>
                  <td>
                    <button onClick={() => alert(`Reativar ID: ${row.id}`)}>
                      Reativar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
        <button onClick={onClose}>Fechar</button>
      </div>
    </div>
  );
};

export default ReactivationModal;
