import React, { useEffect, useState } from 'react';
import { FaEdit, FaTrashAlt, FaUsers, FaCheck } from 'react-icons/fa';

const Table = ({ table, searchTerm, status, onEdit, onViewRelated }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchData();
  }, [table, searchTerm, status]);

  const fetchData = () => {
    const apiUrl = 'http://localhost:5000'; // URL base
    setIsLoading(true);
    fetch(`${apiUrl}/api/${table}?status=${status}&name=${searchTerm}`)
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao buscar dados.');
        return response.json();
      })
      .then((data) => setData(data))
      .catch((err) => setError(err.message))
      .finally(() => setIsLoading(false));
  };

  const handleDeactivate = (id) => {
    const apiUrl = 'http://localhost:5000';
    fetch(`${apiUrl}/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then((response) => {
        if (!response.ok) throw new Error('Erro ao desativar registro.');
        fetchData();
        alert('Registro desativado com sucesso.');
      })
      .catch((err) => console.error(err.message));
  };

  const renderActions = (row) => (
    <td>
      <FaEdit style={{ cursor: 'pointer', marginRight: '10px', color: 'blue' }} onClick={() => onEdit(row)} />
      {status === 'ativo' ? (
        <FaTrashAlt
          style={{ cursor: 'pointer', marginRight: '10px', color: 'red' }}
          onClick={() => handleDeactivate(row.id)}
        />
      ) : (
        <FaCheck style={{ cursor: 'pointer', marginRight: '10px', color: 'green' }} />
      )}
      {(table === 'turmas' || table === 'alunos') && (
        <FaUsers
          style={{ cursor: 'pointer', color: 'purple' }}
          onClick={() =>
            onViewRelated(
              table === 'turmas' ? 'alunos' : 'turmas',
              row.id,
              table === 'turmas'
                ? `Alunos da turma ${row.nome}`
                : `Turmas do aluno ${row.nome}`
            )
          }
        />
      )}
    </td>
  );

  if (!table) return <p>Selecione uma tabela para visualizar os dados.</p>;

  if (isLoading) return <p>Carregando dados...</p>;

  if (error) return <p className="error-message">{error}</p>;

  const columns = data.length > 0
    ? Object.keys(data[0]).filter((col) => col !== 'status')
    : [];

  return (
    <table className="data-table">
      <thead>
        <tr>
          {columns.map((col) => (
            <th key={col}>{col.toUpperCase()}</th>
          ))}
          <th>AÇÕES</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row) => (
          <tr key={row.id}>
            {columns.map((col) => (
              <td key={col}>{row[col]}</td>
            ))}
            {renderActions(row)}
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
