import React, { useState } from 'react';
import Table from './Table';
import FormModal from './FormModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showFormModal, setShowFormModal] = useState(false);
  const [editingData, setEditingData] = useState(null);
  const [isReactivationMode, setIsReactivationMode] = useState(false);

  const handleTableChange = (e) => {
    setTable(e.target.value);
  };

  const handleDeactivate = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao desativar registro');
        }
        alert('Registro desativado com sucesso');
      })
      .catch((err) => console.error(err.message));
  };

  const handleActivate = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}/activate`, { method: 'PATCH' })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao reativar registro');
        }
        alert('Registro reativado com sucesso');
      })
      .catch((err) => console.error(err.message));
  };

  const handleSave = (formData) => {
    const method = formData.id ? 'PUT' : 'POST';
    const url = formData.id
      ? `http://localhost:5000/api/${table}/${formData.id}`
      : `http://localhost:5000/api/${table}`;

    fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao salvar registro');
        }
        alert('Registro salvo com sucesso');
      })
      .catch((err) => console.error(err.message));
  };

  const toggleReactivationMode = () => {
    setIsReactivationMode(!isReactivationMode);
    setSearchTerm('');
  };

  return (
    <div>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
        <select onChange={handleTableChange} style={{ marginRight: '10px' }}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        {!isReactivationMode && (
          <input
            type="text"
            placeholder="Pesquisar..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ marginRight: '10px' }}
          />
        )}
        {!isReactivationMode && (
          <button
            onClick={() => {
              setEditingData(null);
              setShowFormModal(true);
            }}
            disabled={!table}
            style={{ marginLeft: '10px' }}
          >
            Inserir
          </button>
        )}
      </div>

      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          isReactivationMode={isReactivationMode}
          onEdit={(row) => {
            setEditingData(row);
            setShowFormModal(true);
          }}
          onDeactivate={handleDeactivate}
          onActivate={handleActivate}
        />
      )}

      <button
        onClick={toggleReactivationMode}
        style={{ position: 'fixed', bottom: '20px', right: '20px' }}
      >
        {isReactivationMode ? 'Voltar para Pesquisa' : 'Reativar Itens'}
      </button>

      {showFormModal && (
        <FormModal
          table={table}
          onClose={() => setShowFormModal(false)}
          existingData={editingData}
          onSave={handleSave}
        />
      )}
    </div>
  );
};

export default HomeView;
