import React, { useState } from 'react';
import Table from './Table';
import RelationModal from './RelationModal';

const HomeView = () => {
  const [table, setTable] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [status, setStatus] = useState('ativo');
  const [showRelationModal, setShowRelationModal] = useState(false);
  const [relationProps, setRelationProps] = useState({});

  const handleViewRelated = (relatedTable, mainId, title) => {
    setRelationProps({ relatedTable, mainId, title });
    setShowRelationModal(true);
  };

  return (
    <div className="home-view">
      <div className="top-bar">
        <select onChange={(e) => setTable(e.target.value)} value={table}>
          <option value="">Selecione uma tabela</option>
          <option value="alunos">Alunos</option>
          <option value="disciplinas">Disciplinas</option>
          <option value="professores">Professores</option>
          <option value="salas">Salas</option>
          <option value="turmas">Turmas</option>
        </select>
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {table && (
        <Table
          table={table}
          searchTerm={searchTerm}
          status={status}
          onEdit={() => {}}
          onViewRelated={handleViewRelated}
        />
      )}

      {showRelationModal && (
        <RelationModal
          mainTable={table}
          relatedTable={relationProps.relatedTable}
          mainId={relationProps.mainId}
          title={relationProps.title}
          onClose={() => setShowRelationModal(false)}
        />
      )}
    </div>
  );
};

export default HomeView;
