import React, { useState, useEffect } from 'react';

const FormModal = ({ table, existingData, onClose, onSave }) => {
  const [formData, setFormData] = useState({});

  useEffect(() => {
    if (existingData) {
      setFormData(existingData);
    } else {
      setFormData(getDefaultFieldsForTable());
    }
  }, [existingData]);

  const getDefaultFieldsForTable = () => {
    const defaultFields = {
      alunos: { nome: '', matricula: '', CPF: '', regularidade: 'regular' },
      disciplinas: { nome: '', codigo: '', periodo: '' },
      professores: { nome: '', CPF: '', titulacao: '' },
      salas: { nome: '', local: '', capacidade: '' },
      turmas: {
        nome: '',
        dia_semana: 'Segunda',
        horario_inicio: '',
        horario_termino: '',
        professores_id: '',
        salas_id: '',
        disciplinas_id: '',
      },
    };
    return defaultFields[table] || {};
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(formData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{existingData ? 'Editar' : 'Inserir'} {table}</h2>
        <form onSubmit={handleSubmit}>
          {Object.keys(formData).map((key) =>
            key !== 'id' && key !== 'status' ? (
              <div key={key} className="form-group">
                <label htmlFor={key}>{key}</label>
                <input
                  type={key.includes('id') || key === 'capacidade' || key === 'periodo' ? 'number' : 'text'}
                  id={key}
                  name={key}
                  value={formData[key] || ''}
                  onChange={handleChange}
                  required
                />
              </div>
            ) : null
          )}
          <div className="modal-actions">
            <button type="submit">Salvar</button>
            <button type="button" onClick={onClose}>
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormModal;
