import React, { useEffect, useState } from 'react';
import { FaEdit, FaTrashAlt } from 'react-icons/fa';

const Table = ({ table, searchTerm, showInativos, onEdit }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (table) {
      fetchData();
    }
  }, [table, searchTerm, showInativos]);

  const fetchData = () => {
    setIsLoading(true);

    const queryParam = searchTerm ? `?name=${searchTerm}` : '';
    const statusParam = `status=${showInativos ? 'inativo' : 'ativo'}`;
    const url = `http://localhost:5000/api/${table}?${statusParam}${queryParam}`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Erro ao buscar dados: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setData(data);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setData([]);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  const columns = data.length > 0
    ? Object.keys(data[0]).filter((key) => key !== 'id')
    : [];

  return (
    <div>
      <table>
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col.toUpperCase()}</th>
            ))}
            <th>AÇÕES</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((row) => (
              <tr key={row.id}>
                {columns.map((col) => (
                  <td key={col}>{row[col]}</td>
                ))}
                <td>
                  <FaEdit
                    style={{ cursor: 'pointer', marginRight: '10px', color: 'blue' }}
                    onClick={() => onEdit(row)}
                  />
                  <FaTrashAlt
                    style={{ cursor: 'pointer', color: 'red' }}
                    onClick={() => console.log(`Desativar ${row.id}`)}
                  />
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={columns.length + 1}>Nenhum dado encontrado.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
