import React from 'react';

const Table = ({ table, searchTerm }) => {
  const data = [
    // Simula√ß√£o de dados, substitua pela chamada da API correspondente
    { id: 1, nome: 'Jo√£o Silva', status: 'ativo' },
    { id: 2, nome: 'Maria Oliveira', status: 'ativo' },
  ];

  const filteredData = data.filter((row) =>
    row.nome.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h2>{table ? `Tabela: ${table}` : 'Selecione uma tabela'}</h2>
      {table && (
        <table>
          <thead>
            <tr>
              {Object.keys(data[0]).map((key) => (
                <th key={key}>{key}</th>
              ))}
              <th>A√ß√µes</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((row) => (
              <tr key={row.id}>
                {Object.values(row).map((value, index) => (
                  <td key={index}>{value}</td>
                ))}
                <td>
                  <button onClick={() => alert(`Alterar ID: ${row.id}`)}>
                    ‚úèÔ∏è
                  </button>
                  <button onClick={() => alert(`Excluir ID: ${row.id}`)}>
                    üóëÔ∏è
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Table;
