import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (table) {
      setIsLoading(true);
      const url = searchTerm
        ? `http://localhost:5000/api/${table}/search?name=${searchTerm}`
        : `http://localhost:5000/api/${table}`;

      fetch(url)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Erro ao buscar dados: ${response.statusText}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log('Dados recebidos:', data); // Log dos dados
          setData(data);
          setError(null);
        })
        .catch((err) => {
          console.error('Erro ao buscar dados:', err);
          setError(err.message);
          setData([]);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }, [table, searchTerm]);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then(() => {
        setData((prevData) => prevData.filter((item) => item.id !== id));
        alert('Registro desativado com sucesso!');
      })
      .catch((err) => console.error('Erro ao desativar registro:', err));
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  const filteredColumns = data.length > 0
    ? Object.keys(data[0]).filter((key) => key !== 'id' && key !== 'status')
    : [];

  return (
    <table>
      <thead>
        <tr>
          {filteredColumns.map((key) => (
            <th key={key}>{key}</th>
          ))}
          <th>AÃ§Ãµes</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row) => (
          <tr key={row.turma}>
            {filteredColumns.map((key) => (
              <td key={key}>{row[key]}</td>
            ))}
            <td>
              <button onClick={() => handleDelete(row.turma)}>ðŸ—‘</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
