import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(null);
  const [editData, setEditData] = useState({});
  const [showConfirmDelete, setShowConfirmDelete] = useState(null);

  // Fetch data for the selected table
  useEffect(() => {
    if (table) {
      setIsLoading(true);
      const url = `http://localhost:5000/api/${table}`;
      const queryParam = searchTerm ? `?name=${searchTerm}` : '';
      const fullUrl = url + queryParam;

      fetch(fullUrl)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Erro ao buscar dados: ${response.statusText}`);
          }
          return response.json();
        })
        .then((data) => {
          setData(data);
          setError(null);
        })
        .catch((err) => {
          console.error('Erro ao buscar dados:', err);
          setError(err.message);
          setData([]);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }, [table, searchTerm]);

  // Handle edit icon click
  const handleEdit = (row) => {
    setIsEditing(row.id);
    setEditData({ ...row });
  };

  // Handle save after editing
  const handleSave = () => {
    fetch(`http://localhost:5000/api/${table}/${isEditing}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao salvar altera√ß√µes.');
        }
        return response.json();
      })
      .then(() => {
        setIsEditing(null);
        setEditData({});
        // Refresh table data
        const url = `http://localhost:5000/api/${table}`;
        fetch(url)
          .then((res) => res.json())
          .then((data) => setData(data));
      })
      .catch((err) => {
        console.error('Erro ao salvar altera√ß√µes:', err);
        setError(err.message);
      });
  };

  // Handle delete icon click
  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}`, { method: 'DELETE' })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao excluir registro.');
        }
        return response.json();
      })
      .then(() => {
        setShowConfirmDelete(null);
        // Refresh table data
        const url = `http://localhost:5000/api/${table}`;
        fetch(url)
          .then((res) => res.json())
          .then((data) => setData(data));
      })
      .catch((err) => {
        console.error('Erro ao excluir registro:', err);
        setError(err.message);
      });
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  const customColumns = data.length > 0
    ? Object.keys(data[0]).filter((key) => key !== 'id' && key !== 'status')
    : [];

  return (
    <>
      <table>
        <thead>
          <tr>
            {customColumns.map((key) => (
              <th key={key}>{key}</th>
            ))}
            <th>A√ß√µes</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((row) => (
              <tr key={row.id}>
                {customColumns.map((key) => (
                  <td key={key}>
                    {isEditing === row.id ? (
                      <input
                        type="text"
                        value={editData[key] || ''}
                        onChange={(e) =>
                          setEditData((prev) => ({ ...prev, [key]: e.target.value }))
                        }
                      />
                    ) : (
                      row[key]
                    )}
                  </td>
                ))}
                <td>
                  {isEditing === row.id ? (
                    <>
                      <button onClick={handleSave}>Salvar</button>
                      <button onClick={() => setIsEditing(null)}>Cancelar</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => handleEdit(row)}>‚úèÔ∏è</button>
                      <button onClick={() => setShowConfirmDelete(row.id)}>üóëÔ∏è</button>
                    </>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={customColumns.length + 1}>Nenhum dado encontrado.</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Confirm Delete Modal */}
      {showConfirmDelete && (
        <div>
          <p>Tem certeza de que deseja excluir este registro?</p>
          <button onClick={() => handleDelete(showConfirmDelete)}>Sim</button>
          <button onClick={() => setShowConfirmDelete(null)}>Cancelar</button>
        </div>
      )}
    </>
  );
};

export default Table;
