import React, { useState, useEffect } from 'react';

const ReactivationModal = ({ table, onClose, onReactivate }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetchInactiveData();
  }, [table]);

  const fetchInactiveData = () => {
    fetch(`http://localhost:5000/api/${table}/inactive`)
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((err) => console.error(err));
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h3>Reativar Itens da Tabela {table.toUpperCase()}</h3>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Ação</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.nome}</td>
                <td>
                  <button onClick={() => onReactivate(item.id)}>Reativar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button onClick={onClose}>Fechar</button>
      </div>
    </div>
  );
};

export default ReactivationModal;
