import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    if (table) {
      const url = searchTerm
        ? `http://localhost:5000/api/${table}/search?name=${searchTerm}`
        : `/api/${table}`;
      fetch(url)
        .then((res) => {
          if (!res.ok) {
            throw new Error('Erro ao carregar dados');
          }
          return res.json();
        })
        .then((data) => setData(data))
        .catch((err) => console.error('Erro ao carregar dados:', err));
    }
  }, [table, searchTerm]);

  const handleDelete = (id) => {
    fetch(`/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then((res) => {
        if (!res.ok) {
          throw new Error('Erro ao desativar o registro');
        }
        setData((prevData) => prevData.filter((item) => item.id !== id));
        alert('Registro desativado com sucesso!');
      })
      .catch((err) => console.error('Erro ao desativar registro:', err));
  };

  return (
    <table border="1" style={{ width: '100%', marginTop: '20px' }}>
      <thead>
        <tr>
          {data.length > 0 &&
            Object.keys(data[0]).map((key) => <th key={key}>{key}</th>)}
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        {data.map((row) => (
          <tr key={row.id}>
            {Object.values(row).map((value, idx) => (
              <td key={idx}>{value}</td>
            ))}
            <td>
              <button onClick={() => handleDelete(row.id)}>Excluir</button>
              {/* Você pode adicionar outro botão para editar aqui */}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default Table;
