import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm, isReactivationMode, onEdit, onDeactivate, onActivate }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (table) {
      fetchData();
    }
  }, [table, searchTerm, isReactivationMode]);

  const fetchData = () => {
    setIsLoading(true);

    const queryParam = searchTerm ? `?name=${searchTerm}` : '';
    const url = isReactivationMode
      ? `http://localhost:5000/api/${table}/inativos${queryParam}`
      : `http://localhost:5000/api/${table}${queryParam}`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Erro ao buscar dados: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setData(data);
        setError(null);
      })
      .catch((err) => {
        setError(err.message);
        setData([]);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  const renderActions = (row) => (
    <td>
      {isReactivationMode ? (
        <button onClick={() => onActivate(row.id)}>Reativar</button>
      ) : (
        <>
          <button onClick={() => onEdit(row)}>Editar</button>
          <button onClick={() => onDeactivate(row.id)}>Desativar</button>
        </>
      )}
    </td>
  );

  return (
    <div>
      {isLoading && <p>Carregando dados...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!isLoading && !error && (
        <table>
          <thead>
            <tr>
              {data.length > 0 &&
                Object.keys(data[0]).map((col) => <th key={col}>{col.toUpperCase()}</th>)}
              <th>AÇÕES</th>
            </tr>
          </thead>
          <tbody>
            {data.length > 0 ? (
              data.map((row) => (
                <tr key={row.id}>
                  {Object.keys(row).map((col) => (
                    <td key={col}>{row[col]}</td>
                  ))}
                  {renderActions(row)}
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="100%">Nenhum dado encontrado.</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Table;
