import React, { useEffect, useState } from 'react';

const Table = ({ table }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    if (table) {
      // Simulação de chamada à API
      fetch(`/api/${table}`)
        .then((res) => res.json())
        .then((data) => setData(data))
        .catch((err) => console.error(`Erro ao carregar dados da tabela ${table}:`, err));
    }
  }, [table]);

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (data.length === 0) {
    return <p>Nenhum dado disponível na tabela {table}.</p>;
  }

  return (
    <div>
      <h2>Tabela: {table}</h2>
      <table>
        <thead>
          <tr>
            {Object.keys(data[0]).map((key) => (
              <th key={key}>{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {Object.values(row).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
