import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (table) {
      setIsLoading(true);
      const url = searchTerm
        ? `http://localhost:5000/api/${table}/search?name=${searchTerm}`
        : `http://localhost:5000/api/${table}`;

      fetch(url)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Erro ao buscar dados: ${response.statusText}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log('Dados recebidos:', data); // Log para depuraÃ§Ã£o
          setData(data);
          setError(null);
        })
        .catch((err) => {
          console.error('Erro ao buscar dados:', err);
          setError(err.message);
          setData([]);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }, [table, searchTerm]);

  const handleDelete = (id) => {
    fetch(`http://localhost:5000/api/${table}/${id}/deactivate`, { method: 'PATCH' })
      .then(() => {
        setData((prevData) => prevData.filter((item) => item.id !== id));
        alert('Registro desativado com sucesso!');
      })
      .catch((err) => console.error('Erro ao desativar registro:', err));
  };

  const handleEdit = (id) => {
    alert(`FunÃ§Ã£o de ediÃ§Ã£o chamada para ID: ${id}`);
    // Aqui vocÃª pode abrir um modal para ediÃ§Ã£o dos dados do registro
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  // Definir colunas personalizadas para "turmas"
  const isTurmas = table === 'turmas';
  const customColumns = isTurmas
    ? ['turma', 'professor', 'sala', 'disciplina', 'dia_semana', 'horario_inicio', 'horario_termino']
    : data.length > 0
    ? Object.keys(data[0]).filter((key) => key !== 'id' && key !== 'status')
    : [];

  return (
    <table>
      <thead>
        <tr>
          {customColumns.map((key) => (
            <th key={key}>{key}</th>
          ))}
          <th>AÃ§Ãµes</th>
        </tr>
      </thead>
      <tbody>
        {data.length > 0 ? (
          data.map((row) => (
            <tr key={row.id || row.turma}>
              {customColumns.map((key) => (
                <td key={key}>{row[key]}</td>
              ))}
              <td>
                <button onClick={() => handleEdit(row.id || row.turma)} title="Alterar">
                  ðŸ–‰
                </button>
                <button onClick={() => handleDelete(row.id || row.turma)} title="Excluir">
                  ðŸ—‘
                </button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan={customColumns.length + 1}>Nenhum dado encontrado.</td>
          </tr>
        )}
      </tbody>
    </table>
  );
};

export default Table;
