import React, { useEffect, useState } from 'react';

const Table = ({ table, searchTerm }) => {
  const [data, setData] = useState([]);
  const [relatedData, setRelatedData] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (table) {
      setIsLoading(true);
      const url = `http://localhost:5000/api/${table}`;
      const queryParam = searchTerm ? `?name=${searchTerm}` : '';
      const fullUrl = url + queryParam;

      fetch(fullUrl)
        .then((response) => {
          if (!response.ok) {
            throw new Error(`Erro ao buscar dados: ${response.statusText}`);
          }
          return response.json();
        })
        .then((data) => {
          setData(data);
          setError(null);
        })
        .catch((err) => {
          console.error('Erro ao buscar dados:', err);
          setError(err.message);
          setData([]);
        })
        .finally(() => {
          setIsLoading(false);
        });
    }
  }, [table, searchTerm]);

  const fetchRelatedData = (id, type) => {
    setIsLoading(true);
    const url = type === 'alunos' 
      ? `http://localhost:5000/api/turmas/${id}/alunos`
      : `http://localhost:5000/api/alunos/${id}/turmas`;

    fetch(url)
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Erro ao buscar dados relacionados: ${response.statusText}`);
        }
        return response.json();
      })
      .then((data) => {
        setRelatedData(data);
        setSelectedId(id);
        setError(null);
      })
      .catch((err) => {
        console.error('Erro ao buscar dados relacionados:', err);
        setError(err.message);
        setRelatedData([]);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

  if (!table) {
    return <p>Selecione uma tabela para visualizar os dados.</p>;
  }

  if (isLoading) {
    return <p>Carregando dados...</p>;
  }

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <>
      <table>
        <thead>
          <tr>
            {data.length > 0 && Object.keys(data[0]).map((key) => <th key={key}>{key}</th>)}
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {data.length > 0 ? (
            data.map((row) => (
              <tr key={row.id}>
                {Object.values(row).map((value, idx) => <td key={idx}>{value}</td>)}
                <td>
                  {table === 'turmas' && (
                    <button onClick={() => fetchRelatedData(row.id, 'alunos')}>👁️ Ver Alunos</button>
                  )}
                  {table === 'alunos' && (
                    <button onClick={() => fetchRelatedData(row.id, 'turmas')}>👁️ Ver Turmas</button>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={data.length + 1}>Nenhum dado encontrado.</td>
            </tr>
          )}
        </tbody>
      </table>

      {relatedData.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <h3>Dados Relacionados</h3>
          <ul>
            {relatedData.map((item, idx) => (
              <li key={idx}>{Object.values(item).join(' - ')}</li>
            ))}
          </ul>
        </div>
      )}
    </>
  );
};

export default Table;
